hudson.tasks.Shell.command:
#!/bin/bash

DISTRIBUTION=${DISTRIBUTION_NAME}
COMPONENT=${COMPONENT_NAME}
TARGETDIR="${WORKSPACE}/${TARGET_VERSION}"
CONFIG="/etc/aptly.conf"

function add_repo() {
  aptly repo add -config=${CONFIG} ${DISTRIBUTION}_${COMPONENT} ${TARGETDIR}
}

mkdir -p ${TARGETDIR}

unzip packages.zip -d ${TARGETDIR}/

rm packages.zip


if [ ${DISTRIBUTION} == "" ] || [ ${COMPONENT} == "" ] || [ ${TARGETDIR} == "" ] || [ ${CONFIG} == "" ]; then
  echo -e "Please put arguments. ( ./{script}.sh {DISTRIBUTION} {COMPONENT} {TARGETDIR} {CONFIG})"
  exit 1
fi

add_repo

rm -rf ${TARGETDIR}/*
